import UIKit

protocol ConversationPresentationLogic {
    func presentTitle(response: Conversation.userTitleLabel.Response)
    func presentMessages(response: Conversation.Messages.Response)
}

class ConversationPresenter: ConversationPresentationLogic {
    
    weak var viewController: ConversationDisplayLogic?
    var worker: ConversationPresenterWorkerMessagesSorting?

    // MARK: presentUserFullName
    
    func presentTitle(response: Conversation.userTitleLabel.Response) {
        let viewModel = Conversation.userTitleLabel.ViewModel(
            fullname: response.fullname,
            isOnline: response.isOnline
        )
        
        viewController?.displayTitle(viewModel: viewModel)
    }
    
    func presentMessages(response: Conversation.Messages.Response) {
        worker = ConversationPresenterWorkerMessagesSorting()
        let sortedData = worker?.sortMessages(rawMessages: response.rawMessages)
        
        let messagesRows = sortedData!.0
        let headersDatesRows = sortedData!.1
        let lastMessageSelfSender = sortedData!.2
        
        let viewModel = Conversation.Messages.ViewModel(
            messagesRows: messagesRows,
            headersDatesRows: headersDatesRows,
            lastMessageSelfSender: lastMessageSelfSender ?? false
        )
        
        viewController?.displayMessages(viewModel: viewModel)
    }
}
