import UIKit

typealias UserCellViewModel = SearchUser.Search.ViewModel.UserCellViewModel


protocol CellIdentifiable {
    var cellIdentifier: String { get }
}

enum SearchUser {
    // MARK: Use cases
    
    enum Search {
        struct Request {
            var searchText: String?
        }
        
        struct Response {
            var users: [UserModel]
        }
        
        struct ViewModel {
            struct UserCellViewModel: CellIdentifiable {
                var cellIdentifier: String {
                    "UserCell"
                }
                
                let fullName: String
                let username: String
                let email: String
                
                init(user: UserModel) {
                    self.fullName = "\(user.name) \(user.surname)"
                    self.username = user.username
                    self.email = user.email
                }
            }
            
            let rows: [CellIdentifiable]
        }
    }
    
    enum Selected {
        struct Request {
            var selectedUser: CellIdentifiable
        }
    }
}
